
// =======================
// CLASS (OOP)
// =======================

class Product{

    constructor(id,name,price,category,image,desc){
        this.id=id;
        this.name=name;
        this.price=price;
        this.category=category;
        this.image=image;
        this.desc=desc;
    }

}

// OBJECT ARRAY
