let cart=[];


// =======================
// LOAD PRODUCTS (LOOP)
// =======================

function displayProducts(list){

let container=document.getElementById("productContainer");

container.innerHTML="";

for(let p of list){

container.innerHTML+=`

<div class="col-md-3">

<div class="card p-3 mb-3">

<img src="${p.image}" class="card-img-top mb-3" alt="${p.name}">

<h5>${p.name}</h5>

<p class="price"> ${p.price}</p>

<button class="btn btn-success"
onclick="addToCart(${p.id})">
Add To Cart
</button>

</div>

</div>

`;

}

}


// =======================
// ADD TO CART (CONDITION)
// =======================

function addToCart(id){

let product=products.find(p=>p.id===id);

let existing=cart.find(item=>item.id===id);

if(existing){

existing.qty+=1;

}else{

cart.push({ ...product , qty:1 });

}

updateCartCount();

}


// =======================
// UPDATE CART COUNT
// =======================

function updateCartCount(){

let totalQty=0;

for(let item of cart){

totalQty+=item.qty; // operator

}

document.getElementById("cartCount").innerText=totalQty;

}


// =======================
// SHOW CART (FUNCTION)
// =======================

function showCart(){

let modal=new bootstrap.Modal(document.getElementById('cartModal'));

let html="";

let total=0;

for(let item of cart){

html+=`
<p>
${item.name} x ${item.qty}
<button class="btn btn-sm btn-danger"
onclick="removeItem(${item.id})">
Remove
</button>
</p>
`;

total+=item.price*item.qty;

}

if(cart.length===0){

html="Cart empty";

}else{

html+=`<hr><h5>Total  ${total}</h5>`;

}

document.getElementById("cartItems").innerHTML=html;

modal.show();

}


// =======================
// REMOVE ITEM (EVENT)
// =======================

function removeItem(id){

cart=cart.filter(item=>item.id!==id);

updateCartCount();

showCart();

}


// =======================
// FILTER CATEGORY
// =======================

function filterCategory(cat){

if(cat==="all"){

displayProducts(products);

}else{

let filtered=products.filter(p=>p.category===cat);

displayProducts(filtered);

}

}


// =======================
// SEARCH FEATURE (EVENT)
// =======================

document.getElementById("searchBox").addEventListener("keyup",function(){

let value=this.value.toLowerCase();

let filtered=products.filter(p=>p.name.toLowerCase().includes(value));

displayProducts(filtered);

});


// PAGE LOAD EVENT

document.addEventListener("DOMContentLoaded",()=>{

displayProducts(products);

});
